package thegame;

/**
 * @author 160071X
 * The class of result generated by treasure chest
 */
public class Result {
    private Warrior winner;
    private long finishingTime;

    /**
     * Constructor
     *
     * @param winner        warrior who reached the chest first
     * @param finishingTime system time when winner comes to the chest
     */
    public Result(Warrior winner, long finishingTime) {
        this.finishingTime = finishingTime;
        this.winner = winner;
    }

    /**
     * this method is used to get the winner
     *
     * @return winner
     */
    public Warrior getWinner() {
        return winner;
    }

    /**
     * this method is used to get the finishing time
     *
     * @return system time when warrior reaches the chest
     */
    public long getFinishingTime() {
        return finishingTime;
    }
}
